import { useContext, useEffect, useState } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import UserContext from "../UserContext";
import Swal from "sweetalert2";

export default function Profile() {
    const { user } = useContext(UserContext);
    const [userData, setUserData] = useState({
        firstName: "",
        middleName: "",
        lastName: "",
        email: "",
        contactNumber: "",
    });

    const [passwordData, setPasswordData] = useState({
        oldPassword: "",
        newPassword: "",
        confirmPassword: "",
    });

    const [isEditing, setIsEditing] = useState(false);

    // Fetch user details
    const fetchUserDetails = () => {
        fetch("http://localhost:4000/users/details", {
            method: "POST",
            headers: {
                Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
        })
            .then((result) => result.json())
            .then((data) => {
                if (data.result) {
                    setUserData(data.result);
                } else {
                    Swal.fire({
                        title: "Error!",
                        text: "Unable to fetch user details",
                        icon: "error",
                    });
                }
            });
    };

    useEffect(() => {
        fetchUserDetails();
    }, []);

    // Save updated details
    const saveDetails = (e) => {
        e.preventDefault();

        fetch("http://localhost:4000/users/update", {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
            body: JSON.stringify(userData),
        })
            .then((result) => result.json())
            .then((data) => {
                if (data.code === "PROFILE-UPDATED") {
                    Swal.fire({
                        title: "Success!",
                        text: "Profile updated successfully.",
                        icon: "success",
                    });
                    setIsEditing(false); // Exit edit mode
                    fetchUserDetails(); // Refresh profile data
                } else {
                    Swal.fire({
                        title: "Error!",
                        text: data.message || "Unable to update profile.",
                        icon: "error",
                    });
                }
            })
            .catch(() => {
                Swal.fire({
                    title: "Error!",
                    text: "An unexpected error occurred.",
                    icon: "error",
                });
            });
    };

    // Save password change
    const changePassword = (e) => {
        e.preventDefault();

        if (passwordData.newPassword !== passwordData.confirmPassword) {
            Swal.fire({
                title: "Error!",
                text: "New password and confirm password do not match.",
                icon: "error",
            });
            return;
        }

        fetch("http://localhost:4000/users/change-password", {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
            body: JSON.stringify(passwordData),
        })
            .then((result) => result.json())
            .then((data) => {
                if (data.success) {
                    Swal.fire({
                        title: "Success!",
                        text: "Password changed successfully",
                        icon: "success",
                    });
                    setPasswordData({
                        oldPassword: "",
                        newPassword: "",
                        confirmPassword: "",
                    });
                } else {
                    Swal.fire({
                        title: "Error!",
                        text: data.message || "Unable to change password.",
                        icon: "error",
                    });
                }
            });
    };

    return (
        <Container className="vh-100 p-5 d-flex flex-column align-items-center justify-content-center">
            <h1 className="display-4 fw-bold mb-4">User Profile</h1>
            <Form className="w-50 shadow p-5 rounded-3 border-bottom border-3 border-warning" onSubmit={saveDetails}>
                {/* Existing Profile Fields */}
                <Form.Group className="mb-3" controlId="profileFirstName">
                    <Form.Label>First Name</Form.Label>
                    <Form.Control
                        type="text"
                        value={userData.firstName}
                        onChange={(e) => setUserData({ ...userData, firstName: e.target.value })}
                        disabled={!isEditing}
                        required
                    />
                </Form.Group>

                <Form.Group className="mb-3" controlId="profileMiddleName">
                    <Form.Label>Middle Name</Form.Label>
                    <Form.Control
                        type="text"
                        value={userData.middleName}
                        onChange={(e) => setUserData({ ...userData, middleName: e.target.value })}
                        disabled={!isEditing}
                    />
                </Form.Group>

                <Form.Group className="mb-3" controlId="profileLastName">
                    <Form.Label>Last Name</Form.Label>
                    <Form.Control
                        type="text"
                        value={userData.lastName}
                        onChange={(e) => setUserData({ ...userData, lastName: e.target.value })}
                        disabled={!isEditing}
                        required
                    />
                </Form.Group>

                <Form.Group className="mb-3" controlId="profileEmail">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                        type="email"
                        value={userData.email}
                        onChange={(e) => setUserData({ ...userData, email: e.target.value })}
                        disabled={!isEditing}
                        required
                    />
                </Form.Group>

                <Form.Group className="mb-3" controlId="profileContactNumber">
                    <Form.Label>Contact Number</Form.Label>
                    <Form.Control
                        type="text"
                        value={userData.contactNumber}
                        onChange={(e) => setUserData({ ...userData, contactNumber: e.target.value })}
                        disabled={!isEditing}
                    />
                </Form.Group>

                <Row>
                    <Col>
                        {isEditing ? (
                            <>
                                <Button className="w-100 mb-2" type="submit" variant="success">
                                    Save Changes
                                </Button>
                                <Button
                                    className="w-100"
                                    variant="secondary"
                                    onClick={() => {
                                        fetchUserDetails(); // Reload original data
                                        setIsEditing(false); // Exit edit mode
                                    }}
                                >
                                    Cancel
                                </Button>
                            </>
                        ) : (
                            <Button className="w-100" variant="primary" onClick={() => setIsEditing(true)}>
                                Edit Profile
                            </Button>
                        )}
                    </Col>
                </Row>
            </Form>

            {/* Password Change Button */}
            <Button className="w-50 mt-4" variant="warning" onClick={changePassword}>
                Change Password
            </Button>
        </Container>
    );
}
